CREATE TABLE d_products (
    products_id INTEGER IDENTITY(1,1) PRIMARY KEY, 
    products_name VARCHAR(256) NOT NULL,
    products_desc VARCHAR(256) NOT NULL,
    cost DECIMAL(10, 2) NOT NULL,
    price DECIMAL(10, 2) NOT NULL,
    thumbnail_url VARCHAR(256) NOT NULL,
    image_url VARCHAR(256) NOT NULL,
    categories_id INTEGER FOREIGN KEY REFERENCES d_categories(categories_id)
);


